import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fileSystemService, authService } from '../api';
import { FaFolder, FaFile, FaArrowUp, FaPlus, FaTrash, FaEdit, FaPen, FaSave } from 'react-icons/fa';
import './FileExplorer.css';

const FileExplorer = () => {
  const [files, setFiles] = useState([]);
  const [currentDir, setCurrentDir] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCreateFolder, setShowCreateFolder] = useState(false);
  const [showCreateFile, setShowCreateFile] = useState(false);
  const [newFolderName, setNewFolderName] = useState('');
  const [newFileName, setNewFileName] = useState('');
  const [newFileContent, setNewFileContent] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileContent, setFileContent] = useState('');
  const [editMode, setEditMode] = useState(false);
  const navigate = useNavigate();

  const fetchFiles = async () => {
    try {
      setLoading(true);
      setError('');
      
      // Fetch current directory first
      const dirResponse = await fileSystemService.getCurrentDirectory();
      setCurrentDir(dirResponse.data);
      
      // Then fetch files in that directory
      const response = await fileSystemService.listFiles();
      setFiles(response.data.items || []);
    } catch (err) {
      console.error('Error fetching files:', err);
      setError('Failed to load files. Please try again.');
      if (err.response?.status === 401) {
        // Handle unauthorized
        handleLogout();
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFiles();
  }, []);

  const handleLogout = () => {
    authService.logout();
    navigate('/');
  };

  const handleNavigate = async (path) => {
    try {
      await fileSystemService.changeDirectory(path);
      fetchFiles();
      setSelectedFile(null);
      setFileContent('');
      setEditMode(false);
    } catch (err) {
      setError(`Failed to navigate to ${path}`);
    }
  };

  const handleNavigateUp = async () => {
    try {
      await fileSystemService.moveUpDirectory();
      fetchFiles();
      setSelectedFile(null);
      setFileContent('');
      setEditMode(false);
    } catch (err) {
      setError('Failed to navigate up');
    }
  };

  const handleCreateFolder = async () => {
    if (!newFolderName.trim()) {
      setError('Folder name cannot be empty');
      return;
    }
    
    try {
      await fileSystemService.createDirectory(newFolderName);
      setNewFolderName('');
      setShowCreateFolder(false);
      fetchFiles();
    } catch (err) {
      setError('Failed to create folder');
    }
  };

  const handleCreateFile = async () => {
    if (!newFileName.trim()) {
      setError('File name cannot be empty');
      return;
    }
    
    try {
      await fileSystemService.createFile(newFileName, newFileContent);
      setNewFileName('');
      setNewFileContent('');
      setShowCreateFile(false);
      fetchFiles();
    } catch (err) {
      setError('Failed to create file');
    }
  };

  const handleDeleteItem = async (name, isDirectory) => {
    try {
      if (isDirectory) {
        await fileSystemService.deleteDirectory(name);
      } else {
        await fileSystemService.deleteFile(name);
      }
      fetchFiles();
      if (selectedFile === name) {
        setSelectedFile(null);
        setFileContent('');
        setEditMode(false);
      }
    } catch (err) {
      setError(`Failed to delete ${isDirectory ? 'folder' : 'file'}`);
    }
  };

  const handleFileClick = async (fileName) => {
    try {
      setSelectedFile(fileName);
      setEditMode(false);
      const response = await fileSystemService.readFile(fileName);
      setFileContent(response.data);
    } catch (err) {
      setError('Failed to read file');
      setFileContent('');
    }
  };

  const handleSaveFile = async () => {
    try {
      await fileSystemService.editFile(selectedFile, fileContent);
      setEditMode(false);
      setError('');
    } catch (err) {
      setError('Failed to save file');
    }
  };

  return (
    <div className="file-explorer">
      <div className="explorer-header">
        <h2>File System Explorer</h2>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      <div className="path-navigation">
        <div className="current-path">
          <strong>Current Path:</strong> {currentDir}
        </div>
        <button onClick={handleNavigateUp} className="nav-btn">
          <FaArrowUp /> Up
        </button>
      </div>
      
      <div className="actions">
        <button onClick={() => setShowCreateFolder(true)} className="action-btn">
          <FaFolder /> New Folder
        </button>
        <button onClick={() => setShowCreateFile(true)} className="action-btn">
          <FaFile /> New File
        </button>
      </div>
      
      {showCreateFolder && (
        <div className="modal">
          <div className="modal-content">
            <h3>Create New Folder</h3>
            <input
              type="text"
              placeholder="Folder Name"
              value={newFolderName}
              onChange={(e) => setNewFolderName(e.target.value)}
            />
            <div className="modal-actions">
              <button onClick={handleCreateFolder}>Create</button>
              <button onClick={() => setShowCreateFolder(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
      
      {showCreateFile && (
        <div className="modal">
          <div className="modal-content">
            <h3>Create New File</h3>
            <input
              type="text"
              placeholder="File Name"
              value={newFileName}
              onChange={(e) => setNewFileName(e.target.value)}
            />
            <textarea
              placeholder="File Content"
              value={newFileContent}
              onChange={(e) => setNewFileContent(e.target.value)}
            />
            <div className="modal-actions">
              <button onClick={handleCreateFile}>Create</button>
              <button onClick={() => setShowCreateFile(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
      
      <div className="explorer-container">
        <div className="file-list">
          <h3>Files and Folders</h3>
          {loading ? (
            <div className="loading">Loading...</div>
          ) : (
            <ul>
              {files.length === 0 ? (
                <li className="empty-message">No files or folders found</li>
              ) : (
                files.map((item) => (
                  <li key={item.name} className={`file-item ${selectedFile === item.name ? 'selected' : ''}`}>
                    <div 
                      className="file-name"
                      onClick={() => item.type === 'directory' 
                        ? handleNavigate(item.name) 
                        : handleFileClick(item.name)
                      }
                    >
                      {item.type === 'directory' ? <FaFolder className="icon folder" /> : <FaFile className="icon file" />}
                      {item.name}
                    </div>
                    <div className="file-actions">
                      <button 
                        onClick={() => handleDeleteItem(item.name, item.type === 'directory')}
                        className="file-action-btn delete"
                      >
                        <FaTrash />
                      </button>
                    </div>
                  </li>
                ))
              )}
            </ul>
          )}
        </div>
        
        <div className="file-preview">
          <h3>
            {selectedFile ? (
              <>
                <span>File: {selectedFile}</span>
                <div className="file-preview-actions">
                  {editMode ? (
                    <button onClick={handleSaveFile} className="edit-btn save">
                      <FaSave /> Save
                    </button>
                  ) : (
                    <button onClick={() => setEditMode(true)} className="edit-btn">
                      <FaEdit /> Edit
                    </button>
                  )}
                </div>
              </>
            ) : (
              'Select a file to preview'
            )}
          </h3>
          {selectedFile && (
            editMode ? (
              <textarea
                className="file-content-editor"
                value={fileContent}
                onChange={(e) => setFileContent(e.target.value)}
              />
            ) : (
              <pre className="file-content">{fileContent}</pre>
            )
          )}
        </div>
      </div>
    </div>
  );
};

export default FileExplorer;
